INSERT INTO `wp_comments` VALUES (3, 4, 'admin', 'webmaster@conferenciarails.org', '', '80.32.157.206', '2008-08-22 12:53:26', '2008-08-22 10:53:26', 'Los enlaces a descarga directa y a varios mirrors están en http://2007.conferenciarails.org \r\nEsos links seguro que funcionan.\r\nUn saludo.', 0, '1', 'Mozilla/5.0 (Windows; U; Windows NT 5.1; en-US; rv:1.9.0.1) Gecko/2008070208 Firefox/3.0.1', '', 0, 1) ; 
