INSERT INTO `wp_comments` VALUES (61, 68, 'Carlos González-Cadenas', 'carlos@gonzalez.name', 'http://www.experienceon.com', '79.151.184.53', '2008-11-12 20:01:33', '2008-11-12 18:01:33', 'En ExperienceOn estamos construyendo un buscador Web de nueva generación, y actualmente estamos completando nuestro equipo técnico. Buscamos ingenieros "top" para trabajar con tecnologías de procesamiento de lenguaje natural (NLP), machine learning, logic programming, reasoning, ... Si estáis interesados, no dudéis en hablar conmigo tanto durante el "speed dating" como en cualquier otro momento. Nos vemos en la conferencia!', 0, '1', 'Mozilla/5.0 (X11; U; Linux x86_64; es-ES; rv:1.9.0.3) Gecko/2008092510 Ubuntu/8.04 (hardy) Firefox/3.0.3', '', 0, 0) ; 
