INSERT INTO `wp_comments` VALUES (205, 69, 'sofjunior', 'prog_castillo@yahoo.es', '', '190.234.68.239', '2009-11-18 15:43:28', '2009-11-18 13:43:28', 'Saludos,\r\nNo saben las ganas que tengo de vivir un par de semanas en España para disfrutar toda la Conferencia Rails 2009. Pues solo me contentaré presenciarla luego en vídeos. Agradezco muchísimo a los responsables de grabar y subir los vídeos. De verdad muchas gracias. Soy de Perú. Un abrazo.', 0, '1', 'Mozilla/5.0 (Windows; U; Windows NT 5.1; en-US) AppleWebKit/532.0 (KHTML, like Gecko) Chrome/3.0.195.33 Safari/532.0', '', 0, 0) ; 
