INSERT INTO `wp_options` VALUES (85, 0, 'rss_57bc725ad6568758915363af670fd8bc', 'O:9:"MagpieRSS":19:{s:6:"parser";i:0;s:12:"current_item";a:0:{}s:5:"items";a:15:{i:0;a:7:{s:5:"title";s:23:"pras.88in on "Spot Map"";s:4:"link";s:56:"http://wordpress.org/extend/plugins/spot-map/#post-19943";s:7:"pubdate";s:31:"Mon, 09 Aug 2010 10:14:01 +0000";s:2:"dc";a:1:{s:7:"creator";s:9:"pras.88in";}s:4:"guid";s:42:"19943@http://wordpress.org/extend/plugins/";s:11:"description";s:113:"feature:\n\ncan add any number of places to google map,the red colored balloons are the places which you have added";s:7:"summary";s:113:"feature:\n\ncan add any number of places to google map,the red colored balloons are the places which you have added";}i:1;a:7:{s:5:"title";s:38:"hughwillfayle on "Twitter Tag Content"";s:4:"link";s:67:"http://wordpress.org/extend/plugins/twitter-tag-content/#post-19946";s:7:"pubdate";s:31:"Mon, 09 Aug 2010 12:10:27 +0000";s:2:"dc";a:1:{s:7:"creator";s:13:"hughwillfayle";}s:4:"guid";s:42:"19946@http://wordpress.org/extend/plugins/";s:11:"description";s:101:"This plugin provides a text widget, which visibility can be hiddened by adding page ids to the widget";s:7:"summary";s:101:"This plugin provides a text widget, which visibility can be hiddened by adding page ids to the widget";}i:2;a:7:{s:5:"title";s:23:"Hytekk on "CJ-Datafeed"";s:4:"link";s:59:"http://wordpress.org/extend/plugins/cj-datafeed/#post-19766";s:7:"pubdate";s:31:"Mon, 02 Aug 2010 01:45:16 +0000";s:2:"dc";a:1:{s:7:"creator";s:6:"Hytekk";}s:4:"guid";s:42:"19766@http://wordpress.org/extend/plugins/";s:11:"description";s:50:"CJ-Datafeed is a great tool for CJ.com affiliates.";s:7:"summary";s:50:"CJ-Datafeed is a great tool for CJ.com affiliates.";}i:3;a:7:{s:5:"title";s:39:"Kawauso on "Restrict Multisite Plugins"";s:4:"link";s:74:"http://wordpress.org/extend/plugins/restrict-multisite-plugins/#post-18844";s:7:"pubdate";s:31:"Sat, 26 Jun 2010 14:39:26 +0000";s:2:"dc";a:1:{s:7:"creator";s:7:"Kawauso";}s:4:"g